import { useState, useEffect } from 'react';
import queryString from 'query-string';

import Order from './Order';
import Button from './components/Button';

import './App.scss';
import Tab from './components/Tab';

function App() {
  const [dataList, setDataList] = useState([]);
  const [page, setPage] = useState('');
  const [activeTab, setActiveTab] = useState(0);
  const [orderStatus, setOrderStatus] = useState('');
  const tabConfig = [
    {
      title: 'Semua Pesanan',
      value: ''
    },
    {
      title: 'Pesanan Baru',
      value: 'WAITING_CONFIRMATION'
    },
    {
      title: 'Siap Berangat',
      value: 'READY_GO'
    },

  ]

  useEffect(() => {
    handleFetchList();
  }, []);

  const handleFetchList = async (params={}) => {
    console.log('params: ', params);
    const qsParams = params ? `?${queryString.stringify(params)}` : '';
    console.log('qsParams: ', qsParams);
    try {
      const response = await fetch(`http://localhost:3001/orderCar/order-list${qsParams}`);
      const result = await response.json();


      if (result.data.code === 'SUCCESS'){
        setDataList(result.data.data.content);
      }
    } catch (err){
      console.log('err', err);
    }
  }

  const onMoreList = () => {
    const newPage = page + 1;
    const payload = { page: newPage, size: 10, orderStatus: orderStatus};

    setPage(newPage);
    handleFetchList(payload);
  }

  const handleChangeTab = tabActive => {
    console.log('handle change tab');
    const params = {
      page: 0,
      size: 10, 
      orderStatus: tabActive
    }

    handleFetchList(params);
    setOrderStatus(tabActive);

  }


  return (
    <div className="App">
      <Tab activeTab={activeTab} onClickTab={handleChangeTab}>
        {
          tabConfig.map((tab, idx) => {
            console.log('tab: ', tab);
            return (
              <Tab.TabPane key={`tabpane-${idx}`} tab={tab} >
                <div className='list-wrapper'>
                  {
                    dataList.map(item => {
                      console.log('dataList: ', dataList);
                      const {orderId, isSameDay, customerName, isWithDriver, phoneNumber, pickupDate, orderDate } = item;
                      return (
                        <Order 
                          orderId={orderId}
                          isSameDay={isSameDay}
                          customerName={customerName}
                          isWithDriver={isWithDriver}
                          phoneNumber={phoneNumber}
                          pickupDate={pickupDate}
                          orderDate={orderDate}
                        />
                      )
                    })
                  }
                  <Button label={'Lihat Lebih Banyak'} secondary onClick={onMoreList}/>
                </div>
              </Tab.TabPane>
            )
          })
        }

      </Tab>
    </div>
  );
}

export default App;
