import { useState, useEffect } from 'react';
import queryString from 'query-string';

import Order from './Order';
import Badge from './components/Badge';

import './App.scss';
import Tab from './components/Tab';
import OrderDetail from './OrderDetail';

function App() {
  const [dataList, setDataList] = useState([]);
  const [dataOrderDetail, setDataOrderDetail] = useState({});
  const [page, setPage] = useState('');
  const [orderStatus, setOrderStatus] = useState('ALL');
  const [activeModal, setActiveModal] = useState('');


  const tabConfig = [
    {
      title: 'Semua Pesanan',
      value: 'ALL'
    },
    {
      title: 'Pesanan Baru',
      value: 'WAITING_CONFIRMATION'
    },
    {
      title: 'Siap Berangkat',
      value: 'READY_GO'
    },

  ]

  useEffect(() => {
    handleFetchList({orderStatus: 'ALL'});
  }, []);

  const handleFetchList = async (params={}) => {
    const qsParams = params ? `?${queryString.stringify(params)}` : '';

    try {
      const response = await fetch(`http://localhost:3001/orderCar/order-list${qsParams}`);
      const result = await response.json();
      console.log('result: ', result);

      if (result.data.code === 'SUCCESS'){
        setDataList(result.data.data.content);
      }
    } catch (err){
      console.log('err', err);
    }
  }

  const handleFetchOrderDetail = async (orderId='') => {
    try {
      const response = await fetch(`http://localhost:3001/orderCar/confirm-order/detail/${orderId}`);
      const result = await response.json();

      if (result.code === 'SUCCESS'){
        setDataOrderDetail(result.data);
      }
    } catch (err){
      console.log('err', err);
    }
  }

  const handleChangeTab = tabActive => {
    const params = {
      page: 0,
      size: 10, 
      orderStatus: tabActive
    }

    handleFetchList(params);
    setOrderStatus(tabActive);
  }

  const handleConfirmation = async (orderId) => {
    await handleFetchOrderDetail(orderId);
    handleOpenPopup(orderId);
  }

  const handleOpenPopup = (orderId) => {
    setActiveModal(orderId);
    document.body.classList.add('overflow-hidden'); 
  }
  const handleClosePopup = () => {
    setActiveModal('');
    document.body.classList.remove('overflow-hidden');
  }


  return (
    <div className='App'>
      <Tab activeTab={orderStatus} onClickTab={handleChangeTab}>
        {
          tabConfig.map((tab, idx) => {
            return (
              <Tab.TabPane key={`tabpane-${idx}`} tab={tab} >
                <div className='list-wrapper block sm:hidden'>
                  {
                    dataList.map(item => {
                      const {orderId, isSameDay, customerName, isWithDriver, phoneNumber, pickupDate, orderDate } = item;
                      return (
                        <Order 
                          orderId={orderId}
                          isSameDay={isSameDay}
                          customerName={customerName}
                          isWithDriver={isWithDriver}
                          phoneNumber={phoneNumber}
                          pickupDate={pickupDate}
                          orderDate={orderDate}
                          onConfirm={handleConfirmation}
                        />
                      )
                    })
                  }
                </div>
                <div className='my-4 hidden sm:block'>
                  <div className='overflow-auto rounded-lg shadow'>
                    <table className="text-xs sm:text-sm w-full">
                      <thead className="bg-gray-50 border-b-2 border-gray-300 text-left">
                        <tr>
                          <th className="p-3 font-semibold tracking-wide">No.</th>
                          <th className="p-3 font-semibold tracking-wide">Order ID</th>
                          <th className="w-6 p-3 font-semibold tracking-wide">Tipe Pesanan</th>
                          <th className="p-3 font-semibold tracking-wide">Tanggal Pesan</th>
                          <th className="p-3 font-semibold tracking-wide">Tanggal & Waktu Jemput</th>
                          <th className="p-3 font-semibold tracking-wide">Nama Pemesan</th>
                          <th className="p-3 font-semibold tracking-wide">Nomor Ponsel</th>
                          <th className="p-3 font-semibold tracking-wide">Email Pemesan</th>
                          <th className="p-3 font-semibold tracking-wide">Transmisi Mobil</th>
                          <th className="p-3 font-semibold tracking-wide">Merk Kendaraan</th>
                          <th className="p-3 font-semibold tracking-wide">Lama Waktu Sewa</th>
                          <th className="p-3 font-semibold tracking-wide">Wilayah</th>
                          <th className="p-3 font-semibold tracking-wide">Tempat Jemput</th>
                          <th className="p-3 font-semibold tracking-wide">Area Pickup</th>
                          <th className="p-3 font-semibold tracking-wide">Area Pemakaian</th>
                          <th className="p-3 font-semibold tracking-wide">Area Drop-off</th>
                          <th className="p-3 font-semibold tracking-wide">Catatan</th>
                        </tr>
                      </thead>
                      <tbody>
                      {
                        dataList.map((item, idx) => {
                          const {orderId, isSameDay, customerName, isWithDriver, phoneNumber, pickupDate, orderDate } = item;
                          return (
                            <tr>
                              <td className="p-3 tracking-wide">{`${idx+1}.`}</td>
                              <td className="p-3 tracking-wide">{orderId}</td>
                              <td className="p-3 tracking-wide">
                                <span>{isWithDriver ? 'Dengan Sopir' : 'Tanpa Sopir'}</span>
                                <Badge text={isSameDay ? 'Pesan untuk Sekarang' : 'Pesanan Regular'} background={isSameDay ? 'bg-yellow-200' : 'bg-blue-200'} color={isSameDay ? 'text-red-600' : 'text-blue-700'}/>
                              </td>
                              <td className="p-3 tracking-wide">{orderDate}</td>
                            </tr>
                          )
                        })
                      }
                      </tbody>
                    </table>
                  </div>  
                </div>
              </Tab.TabPane>
            )
          })
        }
      </Tab>
      { activeModal ? <OrderDetail data={dataOrderDetail} onClose={handleClosePopup}/> : null}
    </div>
  );
}

export default App;
